INSERT INTO `{prefix}_emailtemplate` (`module`, `email_id`, `language`, `from_email`, `copy_to`, `name`, `subject`, `detail`, `last_update`, `last_send`) VALUES ('payment','1','th','','','อีเมลแจ้งการชำระเงิน','แจ้งชำระเงิน %WEBTITLE%','<div style="padding: 40px; margin-right: 40px; display: block; background-color: rgb(247, 247, 247);">\r\n	<table style="width: 100%; border-collapse: collapse;">\r\n		<tbody>\r\n			<tr>\r\n				<th style="border-width: 1px; border-style: none solid; border-color: rgb(59, 89, 152); padding: 5px; text-align: left; color: rgb(255, 255, 255); font-family: tahoma; font-size: 9pt; background-color: rgb(59, 89, 152);">\r\n					แจ้งการชำระเงิน</th>\r\n			</tr>\r\n			<tr>\r\n				<td style="border-width: 1px; border-style: none solid solid; border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(59, 89, 152); padding: 15px; line-height: 1.8em; font-family: tahoma; font-size: 9pt;">\r\n					คุณ <strong>%FNAME% %LNAME%</strong><br />\r\n					<br />\r\n					ได้แจ้งการชำระเงินตามใบสั่งซื้อเลขที่ <strong>%ID%</strong><br />\r\n					เราได้รับแจ้งการทำรายการนี้แล้ว และจะรีบตรวจสอบและดำเนินการส่งสินค้าให้โดยเร็วที่สุด<br />\r\n					<br />\r\n					ด้วยความขอบคุณ <a href="%WEBURL%">%WEBTITLE%</a></td>\r\n			</tr>\r\n			<tr>\r\n				<td style="padding: 15px; color: rgb(153, 153, 153); font-family: tahoma; font-size: 8pt;">\r\n					ข้อความนี้ถูกส่งมาโดยระบบอัตโนมัติเมื่อคุณได้ทำรายการกับเรา จากที่อยู่ IP : %IP% เมื่อ %TIME%</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n','1333775057','0');
